import React, { useState, useEffect } from "react";
import axios from "axios";

const ProductComponent = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch all products
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/products");
      setProducts(response.data.products);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  // Handle search
  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/products/search?query=${searchQuery}`
      );
      setProducts(response.data.products);
    } catch (error) {
      console.error("Error searching products:", error);
    }
  };

  // Handle delete
  const handleDelete = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${productId}`);
      fetchProducts(); // Refresh the list after deletion
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  // Handle update (modal or inline form could be added for editing)
  const handleUpdate = async (productId, updatedData) => {
    try {
      await axios.put(
        `http://localhost:5000/api/products/${productId}`,
        updatedData
      );
      fetchProducts(); // Refresh the list after update
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Product Inventory</h1>
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Search by Product ID"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <table border="1" cellPadding="10" cellSpacing="0" width="100%">
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>{product.productId}</td>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.stock}</td>
              <td>{product.category}</td>
              <td>
                <button
                  onClick={() => handleDelete(product.productId)}
                  style={{ marginRight: "5px" }}
                >
                  Delete
                </button>
                <button
                  onClick={() =>
                    handleUpdate(product.productId, {
                      ...product,
                      stock: product.stock + 10, // Example update
                    })
                  }
                >
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductComponent;
