// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
const Home = () => {
  const totalProducts = 120; // Example stat
  const lowStockItems = 15;  // Example stat
  const totalValue = 450000; // Example stat (in currency)

  return (
    <div className="bg-beige-100 min-h-screen">
      {/* Hero Section */}
      <section className="bg-brown-800 text-white py-10 px-4 text-center">
        <h1 className="text-3xl md:text-5xl font-bold">Welcome to Inventory Management</h1>
        <p className="mt-4 text-sm md:text-lg">Streamline your business operations by managing products, stock levels, and inventory value effectively.</p>
      </section>

      {/* Stats Section */}
      <section className="container mx-auto py-10 px-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Card 1 */}
          <div className="bg-white shadow-lg rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-brown-800">Total Products</h2>
            <p className="mt-4 text-4xl font-bold text-brown-600">{totalProducts}</p>
          </div>

          {/* Card 2 */}
          <div className="bg-white shadow-lg rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-brown-800">Low Stock Items</h2>
            <p className="mt-4 text-4xl font-bold text-brown-600">{lowStockItems}</p>
          </div>

          {/* Card 3 */}
          <div className="bg-white shadow-lg rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-brown-800">Total Inventory Value</h2>
            <p className="mt-4 text-4xl font-bold text-brown-600">â‚¹{totalValue.toLocaleString()}</p>
          </div>
        </div>
      </section>

      {/* About Section */}
      <section className="bg-white py-10 px-4">
        <div className="container mx-auto text-center">
          <h2 className="text-3xl font-bold text-brown-800">Why Choose Our Inventory Management?</h2>
          <p className="mt-4 text-brown-600">Manage your products effortlessly with features like adding, editing, and deleting items, monitoring low-stock levels, and tracking total inventory value in real-time.</p>
          <div className="mt-6 flex justify-center">
            
            
            
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
