import React, { useState, useEffect } from "react";
import axios from "axios";

const ProductComponent = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [newProduct, setNewProduct] = useState({
    productId: "",
    name: "",
    price: "",
    stock: "",
    category: "",
  });
  const [editProduct, setEditProduct] = useState(null);

  // Fetch all products from the database
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/products");
      setProducts(response.data.products);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  // Search for a product by productId
  const handleSearch = async () => {
    if (!searchQuery) {
      fetchProducts(); // If search query is empty, fetch all products
      return;
    }
    try {
      const response = await axios.get(
        `http://localhost:5000/api/products/search?query=${searchQuery}`
      );
      setProducts(response.data.products);
    } catch (error) {
      console.error("Error searching products:", error);
    }
  };

  // Delete a product
  const handleDelete = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${productId}`);
      fetchProducts(); // Refresh the list after deletion
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  // Start editing a product
  const startEdit = (product) => {
    setEditProduct({ ...product }); // Clone the product for editing
  };

  // Handle change in edit form fields
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditProduct((prev) => ({ ...prev, [name]: value }));
  };

  // Save updated product
  const saveEdit = async () => {
    try {
      await axios.put(
        `http://localhost:5000/api/products/${editProduct.productId}`,
        editProduct
      );
      setEditProduct(null); // Close the edit form
      fetchProducts(); // Refresh the list
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  // Add a new product
  const handleAdd = async () => {
    try {
      await axios.post("http://localhost:5000/api/products", newProduct);
      setNewProduct({
        productId: "",
        name: "",
        price: "",
        stock: "",
        category: "",
      }); // Reset form
      fetchProducts(); // Refresh the list
    } catch (error) {
      console.error("Error adding product:", error);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Product Inventory</h1>

      {/* Search and Add Section */}
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Search by Product ID"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <button
          onClick={handleSearch}
          style={{ marginRight: "10px", boxShadow: "2px 2px 5px rgba(0,0,0,0.3)" }}
        >
          Search
        </button>
        <button
          onClick={() => handleAdd()}
          style={{ boxShadow: "2px 2px 5px rgba(0,0,0,0.3)" }}
        >
          Add Product
        </button>
      </div>

      {/* Product Table */}
      <table border="1" cellPadding="10" cellSpacing="0" width="100%">
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>{product.productId}</td>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.stock}</td>
              <td>{product.category}</td>
              <td>
                <button
                  onClick={() => handleDelete(product.productId)}
                  style={{ marginRight: "5px", boxShadow: "2px 2px 5px rgba(0,0,0,0.3)" }}
                >
                  Delete
                </button>
                <button
                  onClick={() => startEdit(product)}
                  style={{ boxShadow: "2px 2px 5px rgba(0,0,0,0.3)" }}
                >
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Product Form */}
      {editProduct && (
        <div style={{ marginTop: "20px" }}>
          <h2>Edit Product</h2>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              saveEdit();
            }}
          >
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={editProduct.name}
                onChange={handleEditChange}
              />
            </label>
            <br />
            <label>
              Price:
              <input
                type="number"
                name="price"
                value={editProduct.price}
                onChange={handleEditChange}
              />
            </label>
            <br />
            <label>
              Stock:
              <input
                type="number"
                name="stock"
                value={editProduct.stock}
                onChange={handleEditChange}
              />
            </label>
            <br />
            <label>
              Category:
              <input
                type="text"
                name="category"
                value={editProduct.category}
                onChange={handleEditChange}
              />
            </label>
            <br />
            <button
              type="submit"
              style={{ marginRight: "10px", boxShadow: "2px 2px 5px rgba(0,0,0,0.3)" }}
            >
              Save
            </button>
            <button
              type="button"
              onClick={() => setEditProduct(null)}
              style={{ boxShadow: "2px 2px 5px rgba(0,0,0,0.3)" }}
            >
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ProductComponent;
