import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Product = () => {
  const [products, setProducts] = useState([]);
  const [searchId, setSearchId] = useState('');
  const [searchedProduct, setSearchedProduct] = useState(null);
  const [editProduct, setEditProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({ productId: '', name: '', price: '', stock: '', category: '' });

  // Fetch all products
  useEffect(() => {
    axios.get('/api/products').then((response) => setProducts(response.data.products));
  }, []);

  // Handle Edit
  const handleEdit = (product) => {
    setEditProduct(product);
  };

  const saveEdit = () => {
    axios.put(`/api/products/${editProduct.productId}`, editProduct).then(() => {
      setProducts((prev) => prev.map((p) => (p.productId === editProduct.productId ? editProduct : p)));
      setEditProduct(null);
    });
  };

  // Handle Delete
  const handleDelete = (productId) => {
    axios.delete(`/api/products/${productId}`).then(() => {
      setProducts((prev) => prev.filter((p) => p.productId !== productId));
    });
  };

  // Handle Search
  const handleSearch = () => {
    axios.get(`/api/products/${searchId}`).then((response) => setSearchedProduct(response.data.product));
  };

  // Handle Add
  const handleAdd = () => {
    axios.post('/api/products', newProduct).then((response) => {
      setProducts((prev) => [...prev, response.data.product]);
      setNewProduct({ productId: '', name: '', price: '', stock: '', category: '' });
    });
  };

  return (
    <div className="p-6">
      {/* Search Bar */}
      <div className="flex items-center mb-4">
        <input
          type="text"
          value={searchId}
          onChange={(e) => setSearchId(e.target.value)}
          placeholder="Search by Product ID"
          className="border rounded p-2 mr-2"
        />
        <button onClick={handleSearch} className="bg-blue-500 text-white px-4 py-2 rounded shadow-md">Search</button>
        <button onClick={() => setNewProduct({ productId: '', name: '', price: '', stock: '', category: '' })} className="ml-4 bg-green-500 text-white px-4 py-2 rounded shadow-md">
          Add Product
        </button>
      </div>

      {/* Searched Product */}
      {searchedProduct && (
        <table className="table-auto w-full mb-4">
          <thead>
            <tr>
              <th>Product ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Stock</th>
              <th>Category</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{searchedProduct.productId}</td>
              <td>{searchedProduct.name}</td>
              <td>{searchedProduct.price}</td>
              <td>{searchedProduct.stock}</td>
              <td>{searchedProduct.category}</td>
            </tr>
          </tbody>
        </table>
      )}

      {/* Product Table */}
      <table className="table-auto w-full mb-4">
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productId}>
              <td>{product.productId}</td>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.stock}</td>
              <td>{product.category}</td>
              <td>
                <button onClick={() => handleEdit(product)} className="bg-yellow-500 text-white px-2 py-1 rounded shadow-md mr-2">Edit</button>
                <button onClick={() => handleDelete(product.productId)} className="bg-red-500 text-white px-2 py-1 rounded shadow-md">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Form */}
      {editProduct && (
        <div className="mb-4">
          <h3>Edit Product</h3>
          <input
            type="text"
            value={editProduct.productId}
            onChange={(e) => setEditProduct({ ...editProduct, productId: e.target.value })}
            placeholder="Product ID"
            className="border rounded p-2 mr-2"
          />
          <input
            type="text"
            value={editProduct.name}
            onChange={(e) => setEditProduct({ ...editProduct, name: e.target.value })}
            placeholder="Name"
            className="border rounded p-2 mr-2"
          />
          <input
            type="number"
            value={editProduct.price}
            onChange={(e) => setEditProduct({ ...editProduct, price: e.target.value })}
            placeholder="Price"
            className="border rounded p-2 mr-2"
          />
          <input
            type="number"
            value={editProduct.stock}
            onChange={(e) => setEditProduct({ ...editProduct, stock: e.target.value })}
            placeholder="Stock"
            className="border rounded p-2 mr-2"
          />
          <input
            type="text"
            value={editProduct.category}
            onChange={(e) => setEditProduct({ ...editProduct, category: e.target.value })}
            placeholder="Category"
            className="border rounded p-2 mr-2"
          />
          <button onClick={saveEdit} className="bg-green-500 text-white px-4 py-2 rounded shadow-md">Save</button>
        </div>
      )}

      {/* Add Form */}
      {newProduct && (
        <div>
          <h3>Add Product</h3>
          <input
            type="text"
            value={newProduct.productId}
            onChange={(e) => setNewProduct({ ...newProduct, productId: e.target.value })}
            placeholder="Product ID"
            className="border rounded p-2 mr-2"
          />
          <input
            type="text"
            value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
            placeholder="Name"
            className="border rounded p-2 mr-2"
          />
          <input
            type="number"
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
            placeholder="Price"
            className="border rounded p-2 mr-2"
          />
          <input
            type="number"
            value={newProduct.stock}
            onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
            placeholder="Stock"
            className="border rounded p-2 mr-2"
          />
          <input
            type="text"
            value={newProduct.category}
            onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}
            placeholder="Category"
            className="border rounded p-2 mr-2"
          />
          <button onClick={handleAdd} className="bg-blue-500 text-white px-4 py-2 rounded shadow-md">Add</button>
        </div>
      )}
    </div>
  );
};

export default Product;
